static_filter.xquery_qual_expr:
depth = 14;
				startDate = DateNewTime( CurDate );
				endDate = DateOffset( startDate, 86400 * depth );

				startMonthDay = lib_base.MonthDayFromDate( startDate );
				endMonthDay = lib_base.MonthDayFromDate( endDate );

				if ( endMonthDay > startMonthDay )
					return '$elem/birthday_month_day >= ' + startMonthDay + ' and $elem/birthday_month_day <= ' + endMonthDay;
				else
					return '( $elem/birthday_month_day >= ' + startMonthDay + ' or $elem/birthday_month_day <= ' + endMonthDay + ' )';

